def read_df():
    df = pd.read_csv('/Users/valler/Python/OX_Thesis/OX_thesis/data_preprocess/Data/merge_data.csv')
    # rewrite the df['hhidpn']
    df['hhidpn'] = ["{:06d}".format(int(x)) + '{:03d}'.format(y) for x, y in zip(df['hhid'], df['pn'])]
    return df

